mapper.select:
SELECT ST.SUBJ_CD, CRT_RT, PREV_CRT_RT, TOP_CRT_RT, AVG_CRT_RT, EXPL_CNT, ANS_QUES_CNT, CRT_QUES_CNT, INCRT_NOTE_NC_CNT, IMPRV_SLV_HABIT_CNT, SKIP_QUES_CNT, CURSORY_QUES_CNT, GUESS_QUES_CNT, MISTAKE_QUES_CNT FROM ( SELECT STUD_ID, GRADE, SUBJ_CD FROM STUD ST CROSS JOIN COMM_SUBJ_CD CS WHERE ST.STUD_ID = #{studId} AND CS.SUBJ_CD LIKE 'C%' AND CS.DEPTH = 1 ) ST LEFT OUTER JOIN ( SELECT STUD_ID, SUBJ_CD, COUNT(STUD_ID) AS EXPL_CNT, CASE WHEN SUM(QUES_CNT) = 0 THEN 0 ELSE ROUND(SUM(CRT_CNT) / SUM(QUES_CNT) * 100) END AS CRT_RT, SUM(QUES_CNT) AS ANS_QUES_CNT, SUM(CRT_CNT) AS CRT_QUES_CNT, IF(IFNULL(SUM(INCRT_NT_REG_CNT),0) = 0, 0, (SUM(INCRT_NT_REG_CNT) - SUM(INCRT_NT_FNSH_CNT))) AS INCRT_NOTE_NC_CNT FROM EXAM_RST WHERE DT BETWEEN #{startDt} AND #{endDt} AND STUD_ID = #{studId} AND DEL_YN = 'N' GROUP BY STUD_ID, SUBJ_CD ) ER ON ST.STUD_ID = ER.STUD_ID AND ST.SUBJ_CD = ER.SUBJ_CD LEFT OUTER JOIN ( SELECT STUD_ID, SUBJ_CD, COUNT(IF(IFNULL(QUES_PTN_IRT, QUES_PTN) IN ('GUC', 'GUI', 'SKP', 'CUR', 'MIS'), 1, NULL)) AS IMPRV_SLV_HABIT_CNT, COUNT(IF(IFNULL(QUES_PTN_IRT, QUES_PTN) = 'SKP', 1, NULL)) AS SKIP_QUES_CNT, COUNT(IF(IFNULL(QUES_PTN_IRT, QUES_PTN) = 'CUR', 1, NULL)) AS CURSORY_QUES_CNT, COUNT(IF(IFNULL(QUES_PTN_IRT, QUES_PTN) IN ('GUC', 'GUI'), 1, NULL)) AS GUESS_QUES_CNT, COUNT(IF(IFNULL(QUES_PTN_IRT, QUES_PTN) = 'MIS', 1, NULL)) AS MISTAKE_QUES_CNT FROM EXAM_RST_QUES_LOG WHERE DT BETWEEN #{startDt} AND #{endDt} AND STUD_ID = #{studId} GROUP BY STUD_ID, SUBJ_CD ) EQ ON ST.STUD_ID = EQ.STUD_ID AND ST.SUBJ_CD = EQ.SUBJ_CD LEFT OUTER JOIN ( SELECT STUD_ID, SUBJ_CD, CASE WHEN SUM(QUES_CNT) = 0 THEN 0 ELSE ROUND(SUM(CRT_CNT) / SUM(QUES_CNT) * 100) END AS PREV_CRT_RT FROM EXAM_RST WHERE DT
BETWEEN DATE_SUB(#{startDt}, INTERVAL 1 MONTH) AND LAST_DAY(DATE_SUB(#{startDt}, INTERVAL 1 MONTH))
BETWEEN DATE_SUB(#{startDt}, INTERVAL 7 DAY) AND DATE_SUB(#{endDt}, INTERVAL 7 DAY)
AND STUD_ID = #{studId} AND DEL_YN = 'N' GROUP BY STUD_ID, SUBJ_CD ) PER ON ST.STUD_ID = PER.STUD_ID AND ST.SUBJ_CD = PER.SUBJ_CD LEFT OUTER JOIN ( SELECT GRADE, SUBJ_CD, NULL AS TOP_CRT_RT, GRP_AVG_CRT_RT AS AVG_CRT_RT
FROM MONTH_GRP_EXAM_SUBJ_RST WHERE YYMM = #{yyMm}
FROM DAY_7_GRP_EXAM_SUBJ_RST WHERE DT = #{endDt}
) GP ON ST.GRADE = GP.GRADE AND ST.SUBJ_CD = GP.SUBJ_CD

SELECT COUNT(STUD_ID) AS TOTAL_CNT FROM ( SELECT ER.STUD_ID FROM EXAM_RST ER LEFT OUTER JOIN EXAM_RST_QUES_LOG EQ ON ER.DT = EQ.DT AND ER.STUD_ID = EQ.STUD_ID AND ER.EXAM_CD = EQ.EXAM_CD AND ER.EXAM_TYPE = EQ.EXAM_TYPE AND ER.SMT_ID = EQ.SMT_ID WHERE ER.DT BETWEEN #{startDt} AND #{endDt} AND ER.STUD_ID = #{studId}
AND ER.EXAM_TYPE IN
#{item}
AND ER.DEL_YN = 'N'
AND ER.EXAM_NM LIKE CONCAT('%',#{search},'%')
GROUP BY ER.STUD_ID, ER.EXAM_CD, ER.SMT_ID ) CNT GROUP BY STUD_ID

SELECT ER.SUBJ_CD, DATE_FORMAT(ER.SMT_DTTM,'%Y-%m-%d %H:%i:%s') AS SMT_DTTM, ER.EXAM_TYPE AS TYPE, ER.EXAM_CD, ER.SMT_ID, ER.STUD_ID, ER.EXAM_NM, CONCAT((SELECT IF(COUNT(*) = 1, 1, COUNT(*) + 1) FROM EXAM_RST WHERE STUD_ID = ER.STUD_ID AND EXAM_CD = ER.EXAM_CD AND SMT_ID = ER.SMT_ID),'차') AS ROUND, ER.CRT_CNT AS CRT_QUES_CNT, ER.QUES_CNT AS QUES_CNT, GROUP_CONCAT(IF(IFNULL(EQ.QUES_PTN_IRT, EQ.QUES_PTN) = 'CRT', EQ.QUES_ORD, NULL) ORDER BY EQ.QUES_ORD SEPARATOR ',') AS CRT_QUES, GROUP_CONCAT(IF(IFNULL(EQ.QUES_PTN_IRT, EQ.QUES_PTN) = 'GUC', EQ.QUES_ORD, NULL) ORDER BY EQ.QUES_ORD SEPARATOR ',') AS GUESS_CRT_QUES, GROUP_CONCAT(IF(IFNULL(EQ.QUES_PTN_IRT, EQ.QUES_PTN) = 'SKP', EQ.QUES_ORD, NULL) ORDER BY EQ.QUES_ORD SEPARATOR ',') AS SKIP_QUES, GROUP_CONCAT(IF(IFNULL(EQ.QUES_PTN_IRT, EQ.QUES_PTN) = 'GUI', EQ.QUES_ORD, NULL) ORDER BY EQ.QUES_ORD SEPARATOR ',') AS GUESS_INC_QUES, GROUP_CONCAT(IF(IFNULL(EQ.QUES_PTN_IRT, EQ.QUES_PTN) = 'CUR', EQ.QUES_ORD, NULL) ORDER BY EQ.QUES_ORD SEPARATOR ',') AS CURSORY_QUES, GROUP_CONCAT(IF(IFNULL(EQ.QUES_PTN_IRT, EQ.QUES_PTN) = 'INC', EQ.QUES_ORD, NULL) ORDER BY EQ.QUES_ORD SEPARATOR ',') AS INCRT_QUES, GROUP_CONCAT(IF(IFNULL(EQ.QUES_PTN_IRT, EQ.QUES_PTN) = 'MIS', EQ.QUES_ORD, NULL) ORDER BY EQ.QUES_ORD SEPARATOR ',') AS MISTAKEN_QUES FROM EXAM_RST ER LEFT OUTER JOIN EXAM_RST_QUES_LOG EQ ON ER.DT = EQ.DT AND ER.STUD_ID = EQ.STUD_ID AND ER.EXAM_CD = EQ.EXAM_CD AND ER.EXAM_TYPE = EQ.EXAM_TYPE AND ER.SMT_ID = EQ.SMT_ID WHERE ER.DT BETWEEN #{startDt} AND #{endDt} AND ER.STUD_ID = #{studId}
AND ER.EXAM_TYPE IN
#{item}
AND ER.DEL_YN = 'N'
AND ER.EXAM_NM LIKE CONCAT('%',#{search},'%')
GROUP BY ER.STUD_ID, ER.EXAM_CD, ER.SMT_ID ORDER BY ER.SMT_DTTM DESC
LIMIT 10 OFFSET ${startIdx}
LIMIT 10 OFFSET 0

    	SELECT
			SUM(INCRT_NT_REG_CNT) AS INCRT_NOTE_CNT,
		    SUM(INCRT_NT_FNSH_CNT) AS INCRT_NOTE_FNSH_CNT,
		    (SUM(INCRT_NT_REG_CNT) - SUM(INCRT_NT_FNSH_CNT)) AS INCRT_NOTE_NC_CNT,
		    (
		    	SELECT 
			    	COUNT(*) 
		    	FROM EXAM_RST_INCRT_NT 
		    	WHERE DT BETWEEN #{startDt} AND #{endDt}
	    		AND STUD_ID = #{studId} 
	    		GROUP BY STUD_ID
    		) AS TOTAL_CNT
		FROM EXAM_RST ER
		WHERE DT BETWEEN #{startDt} AND #{endDt}
			AND STUD_ID = #{studId}

SELECT GRADE_TERM, (SELECT SUBJ_NM FROM COMM_SUBJ_CD WHERE SUBJ_CD = EN.SUBJ_CD AND DEPTH = 1) AS SUBJ_CD, UNIT_NM, EXAM_TYPE AS TYPE, EXAM_CD, SMT_ID, STU_ID, (SELECT CD_NM FROM COMM_CD WHERE GRP = 'LRN_INFO' AND CD = EN.LRN_STT) AS LRN_STT, QUES_CNT, CRT_QUES_CNT, INCRT_CNT, REG_DT, SMT_DTTM FROM EXAM_RST_INCRT_NT EN WHERE DT BETWEEN #{startDt} AND #{endDt} AND STUD_ID = #{studId} ORDER BY EN.SMT_DTTM DESC
LIMIT 10 OFFSET ${startIdx}
LIMIT 10 OFFSET 0
