mapper.select:
/* HamsSales.selectLoginLogList : 일별 출석 로그 전체*/
	SELECT
		DATE_FORMAT(DT, '%Y-%m-%d') AS DT,
		DATE_FORMAT(LOGIN_DTTM, '%Y-%m-%d %H:%i:%s') AS LOGIN_DTTM
	FROM DAY_ATT_LOG
	WHERE
		DT BETWEEN #{startDt} AND #{endDt}
	    AND STUD_ID = #{studId}
	ORDER BY ORD DESC
	LIMIT 10

/* HamsSales.selectLrnPtn : 3일 리포트 학습 패턴*/
	SELECT 
		TOTAL_LRN_SEC
	FROM LRN_EX_DETAIL
	WHERE DT = #{dt}
		AND STUD_ID = #{studId}

/* HamsSales.selectLrnPtnTmln : 3일 리포트 학습 패턴 - 타임라인*/
	SELECT 
		DATE_FORMAT(DT, '%Y.%m.%d') AS DT,
	    TIMESTAMPDIFF(DAY, START_DT, DT) + 1 AS EXP_DAY
	FROM STUD 
	WHERE DT BETWEEN #{dt} - INTERVAL 2 DAY  AND #{dt}
		AND STUD_ID = #{studId}

/* HamsSales.selectLrnTmln : 3일 리포트 학습 패턴 - 타임라인 상세*/ SELECT SUBJ_CD, EX_TM, LRN_NM, LRN_SEC, STD_LRN_SEC, EX_TYPE, DATE_FORMAT(PLAN_DT, '%Y.%m.%d' ) AS PLAN_DT FROM ( SELECT SUBJ_CD, CONCAT(DATE_FORMAT(IFNULL(LRN_START_DTTM,''), '%H:%i'), ' ~ ', DATE_FORMAT(IFNULL(LRN_END_DTTM,''), '%H:%i'))AS EX_TM, CTGR AS LRN_NM, LRN_SEC, 600 AS STD_LRN_SEC, EX_TYPE_CD AS EX_TYPE, PLAN_DT FROM DAY_LRN_TMLN WHERE DT = #{dt} AND STUD_ID = #{studId} UNION ALL SELECT NULL AS SUBJ_CD, NULL AS EX_TM, CTGR AS LRN_NM, NULL AS LRN_SEC, 600 AS STD_LRN_SEC, 2 AS EX_TYPE, PLAN_DT FROM DAY_PLAN_U_EX_STT WHERE DT = #{dt} AND STUD_ID = #{studId} AND LRN_EX_YN = 'N' )A
ORDER BY EX_TYPE
ORDER BY IFNULL(EX_TM,'23:59 ~ 24:00')
