mapper.select:
    	/* StudLrnType.getReportCheck */
    	SELECT
			IF(COUNT(*) = 0, 'N', 'Y') AS DATA_CHECK


		FROM MONTH_REPORT
		WHERE YYMM = #{yymm}


   		FROM WEEK_REPORT
		WHERE YYMM = #{yymm}
			AND WK = #{wk}


    		AND STUD_ID = #{studId};

mapper.insert:
/* StudLrnType.insertReportCheck */
INSERT INTO MONTH_REPORT ( YYMM, STUD_ID, CHECKYN, REG_DTTM, UPD_DTTM ) VALUES ( #{yymm}, #{studId}, "Y", DATE_ADD(NOW(), INTERVAL 9 HOUR), DATE_ADD(NOW(), INTERVAL 9 HOUR) );
INSERT INTO WEEK_REPORT ( YYMM, WK, STUD_ID, CHECKYN, REG_DTTM, UPD_DTTM ) VALUES ( #{yymm}, #{wk}, #{studId}, "Y", DATE_ADD(NOW(), INTERVAL 9 HOUR), DATE_ADD(NOW(), INTERVAL 9 HOUR) );

mapper.delete:
    	/* StudLrnType.deleteReportCheck */


	   		DELETE FROM MONTH_REPORT WHERE YYMM = #{yymm} AND STUD_ID = #{studId};


	    	DELETE FROM WEEK_REPORT WHERE YYMM = #{yymm} AND WK = #{wk} AND STUD_ID = #{studId};

mapper.select:
    	/* StudLrnType.getStudLrnTypeInfoCheck */
    	SELECT
			LRN_STT_CD,
    		END_DT
		FROM STUD
		WHERE STUD_ID = #{studId};
