from lrn_dsb.ds_ag_lrn_use_sts_wly a left outer join lrn_dsb.ds_ag_lrn_plan_exec_sts_wly b on a.yyyymmw_key = b.yyyymmw_key and a.stud_key = b.stud_key and a.ssvc_akey = b.ssvc_akey and a.grp_item_key = b.grp_item_key left outer join lrn_dsb.ds_ag_stud_att_sts_wly c on a.yyyymmw_key = c.yyyymmw_key and a.stud_key = c.stud_key and a.ssvc_akey = c.ssvc_akey left outer join ( select yyyymmw_key, stud_key, ssvc_akey, sum(fnsh_add_lrn_cnt) as add_lrn_cnts from lrn_dsb.ds_ag_lrn_use_sts_wly a where yyyymmw_key = #{yymmwk} and stud_key = #{studId} and ssvc_akey = 4 and grp_item_key between 4001 and 4410 group by yyyymmw_key, stud_key, ssvc_akey ) d on a.yyyymmw_key = d.yyyymmw_key and a.stud_key = d.stud_key and a.ssvc_akey = d.ssvc_akey where a.yyyymmw_key = #{yymmwk}
and a.stud_key = #{studId} and a.ssvc_akey = 4 and a.grp_item_key = 4;

    	select
			b.item_cd as subj_cd,
			round(answ_rt * 100) as ex_rt


			from lrn_dsb.ds_ag_exam_assm_sts_mly a
			join lrn_dsb.ds_dm_grp_item b on a.grp_item_key = b.grp_item_key
