			and a.stud_key = #{studId}
			and a.ssvc_akey = 4
			and a.exam_typ_key = 0
			and (a.exam_grdn_cnt - a.del_exam_grdn_cnt) != 0;

select round(a.answ_rt * 100) as exam_score, c.wnote_tot_cnt, c.wnote_fnsh_cnt, c.wnote_unfnsh_cnt, b.ques_tot_cnt, b.ques_skip_cnt, b.ques_hrry_cnt, b.ques_guss_cnt, b.ques_mstke_cnt
