from lrn_dsb.ds_ag_exam_assm_sts_wly a left outer join lrn_dsb.ds_ag_exam_slv_habit_sts_wly b on a.yyyymmw_key = b.yyyymmw_key and a.stud_key = b.stud_key and a.ssvc_akey = b.ssvc_akey and a.exam_typ_key = b.exam_typ_key and a.grp_item_key = b.grp_item_key left outer join lrn_dsb.ds_ag_exam_wnote_sts_wly c on a.yyyymmw_key = c.yyyymmw_key and a.stud_key = c.stud_key and a.ssvc_akey = c.ssvc_akey and c.exam_typ_key = 3 and a.grp_item_key = c.grp_item_key where a.yyyymmw_key = #{yymmwk}
and a.stud_key = #{studId} and a.ssvc_akey = 4 and a.exam_typ_key = 0 and a.grp_item_key = 4;

