	ym.mm,
	att_rt
from (
	select
		yyyymm_key,
		substring(yyyymm_key::text,5,6)::integer as mm
	from lrndmt.mt_dm_wk_dt
	where yyyymm_key between #{startYymm} and #{endYymm}
		and w = '1'
) ym
left outer join (
