mapper.select:
select b.msg, d.msg as info_msg, c.img_url, c.img_bg_url, b_lrn_cnt, t_lrn_cnt, d_lrn_cnt, b_lrn_rt, t_lrn_rt, d_lrn_rt from lrndmt.mt_dm_stud st left outer join ( select stud_key,
pre_lrn_exec_cnt as b_lrn_cnt, intm_lrn_exec_cnt as t_lrn_cnt, eqtrm_dely_lrn_exec_cnt as d_lrn_cnt, round(pre_lrn_exec_cnt::numeric * 100 / plan_lrn_cnt)::integer as b_lrn_rt, round(intm_lrn_exec_cnt::numeric * 100 / plan_lrn_cnt)::integer as t_lrn_rt, round(eqtrm_dely_lrn_exec_cnt::numeric * 100 / plan_lrn_cnt)::integer as d_lrn_rt from lrn_dsb.ds_ag_lrn_plan_exec_sts_mly a where a.yyyymm_key = #{yymm}
pre_lrn_exec_cnt as b_lrn_cnt, intm_lrn_exec_cnt as t_lrn_cnt, dely_lrn_exec_cnt as d_lrn_cnt, round(pre_lrn_exec_cnt::numeric * 100 / plan_lrn_cnt)::integer as b_lrn_rt, round(intm_lrn_exec_cnt::numeric * 100 / plan_lrn_cnt)::integer as t_lrn_rt, round(dely_lrn_exec_cnt::numeric * 100 / plan_lrn_cnt)::integer as d_lrn_rt from lrn_dsb.ds_ag_lrn_plan_exec_sts_wly a where a.yyyymmw_key = #{yymmwk}
and a.stud_key = #{studId} and a.ssvc_akey = 4 and a.grp_item_key = 4 ) lrn_msg on st.stud_key = lrn_msg.stud_key
cross join lrn_dsb.fn_get_msg_rul_LRNMEHBEXRM(#{yymm}, #{studId}, 4) b cross join lrn_dsb.fn_get_msg_rul_LRNMEHBEXRI(#{yymm}, #{studId}, 4) c cross join lrn_dsb.fn_get_msg_rul_LRNMEHBEXRH() d
cross join lrn_dsb.fn_get_msg_rul_LRNWEHBEXRM(#{yymmwk}, #{studId}, 4) b cross join lrn_dsb.fn_get_msg_rul_LRNWEHBEXRI(#{yymmwk}, #{studId}, 4) c cross join lrn_dsb.fn_get_msg_rul_LRNWEHBEXRH() d
where st.stud_key = #{studId};

select
case when intm_lrn_exec_cnt = greatest(intm_lrn_exec_cnt, pre_lrn_exec_cnt, eqtrm_dely_lrn_exec_cnt) then 'tLrn' when pre_lrn_exec_cnt = greatest(intm_lrn_exec_cnt, pre_lrn_exec_cnt, eqtrm_dely_lrn_exec_cnt) then 'bLrn' when eqtrm_dely_lrn_exec_cnt = greatest(intm_lrn_exec_cnt, pre_lrn_exec_cnt, eqtrm_dely_lrn_exec_cnt) then 'dLrn' else null end as max_nm from lrn_dsb.ds_ag_lrn_plan_exec_sts_mly where yyyymm_key = #{yymm}
case when intm_lrn_exec_cnt = greatest(intm_lrn_exec_cnt, pre_lrn_exec_cnt, dely_lrn_exec_cnt) then 'tLrn' when pre_lrn_exec_cnt = greatest(intm_lrn_exec_cnt, pre_lrn_exec_cnt, dely_lrn_exec_cnt) then 'bLrn' when dely_lrn_exec_cnt = greatest(intm_lrn_exec_cnt, pre_lrn_exec_cnt, dely_lrn_exec_cnt) then 'dLrn' else null end as max_nm from lrn_dsb.ds_ag_lrn_plan_exec_sts_wly where yyyymmw_key = #{yymmwk}
and stud_key = #{studId} and ssvc_akey = 4 and grp_item_key = 4;

