(select count(*) from lrn_dsb.ds_ag_stud_att_sts_wly where yyyymmw_key between #{startYymmwk} and #{endYymmwk} and stud_key = #{studId}) as data_cnt
from lrndmt.mt_dm_stud st left outer join ( select a.stud_key, msg.msg, msg.msg_url as img_url
from lrn_dsb.ds_ag_stud_att_sts_mly a left outer join lrndmt.mt_dm_rul_msg_bdy_es msg on msg.rul_cd = lrn_dsb.fn_get_msg_rul_LRNWATTADRI_cd(a.yyyymm_key, a.stud_key, a.ssvc_akey) where a.yyyymm_key = #{yymm}
from lrn_dsb.ds_ag_stud_att_sts_wly a left outer join lrndmt.mt_dm_rul_msg_bdy_es msg on msg.rul_cd = lrn_dsb.fn_get_msg_rul_LRNWATTADRI_cd(a.yyyymmw_key, a.stud_key, a.ssvc_akey) where a.yyyymmw_key = #{yymmwk}
and a.stud_key = #{studId} and a.ssvc_akey = 4 ) concn_msg on st.stud_key = concn_msg.stud_key where st.stud_key = #{studId};

