from lrn_dsb.ds_ag_lrn_use_sts_wly a join lrn_dsb.ds_dm_grp_item b on a.grp_item_key = b.grp_item_key and b.grp_typ = 'SUBJ_CD' and b.grp_item_key between 4001 and 4410 where a.yyyymmw_key = #{yymmwk}
